import React, { Component } from "react";
import axios from "axios";

class Produto extends Component {
  constructor(props) {
    super(props);
    this.state = {
      descricao: this.descricao,
      valor: this.valor,
      quantidade: this.quantidade
    };
  }

  inserir = () => {
    console.log("");
    const dados = {
      descricao: this.descricao,
      valor: this.valor,
      quantidade: this.quantidade
    };
    console.log(dados);
    axios
      .post("http://localhost:3001/insertCalcado", dados)
      .then(response => {
        this.inserir(response.data);
      })
      .catch(erro => console.log(erro));
  };

  render() {
    return (
      <div id="tabela">
        <form>
          Descrição______Valor__________Quantidade_____ <br></br>{" "}
          <input
            type="text"
            value={this.props.descricao}
            onChange={event => {
              this.setState({ descricao: event.target.value });
            }}
          />
          <input
            type="number"
            value={this.props.valor}
            onChange={event => {
              this.setState({ valor: event.target.value });
            }}
          />
          <input
            type="number"
            value={this.props.quantidade}
            onChange={event => {
              this.setState({ quantidade: event.target.value });
            }}
          />
          <button class="fat" onClick={this.update.valor}>
            Inserir/Atualizar
          </button>
          <button class="fat">Excluir</button>
          <br></br>
        </form>
      </div>
    );
  }
}
export default Produto;
