import React from "react";
import axios from "axios";
import Produto from "./Produto";
import "./index.css";
import MyHeader from "./Header.js";

export default class Container extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      feed: [
        { id: 1, descricao: "scarpin", quantidade: 10, valor: 150 },
        { id: 2, descricao: "oxford", quantidade: 20, valor: 140 },
        { id: 3, descricao: "bota", quantidade: 30, valor: 130 }
      ]
    };
  }

  componentWillMount() {
    axios
      .get("")
      .then(response => {
        console.log(response.data);
        this.setState({
          feed: [
            { id: 1, descricao: "SCARPIN", valor: 100, quantidade: 15 },
            { id: 2, descricao: "OXFORD", valor: 200, quantidade: 20 },
            { id: 3, descricao: "BOTA", valor: 150, quantidade: 30 }
          ]
        });
      })
      .catch(erro => console.log(erro));
  }

  render() {
    return (
      <div>
        <MyHeader />
        {this.state.feed.map(item => (
          <Produto
            id={item.id}
            sapato={item.descricao}
            preco={item.feed.valor}
            lote={item.quantidade}
          />
        ))}

        <h2>Descrição / Valor / Quantidade</h2>
        {this.state.feed.map(produto => {
          return (
            <div key={produto.id} id="lista">
              <h5>
                {" "}
                {produto.descricao} /// {"    "} {produto.valor} /// {"     "}
                {produto.quantidade}
              </h5>
            </div>
          );
        })}
      </div>
    );
  }
}
