import React, { Component } from "react";
import axios from "axios";
import Produto from "./Produto";
import "./index.css";
import MyHeader from "./Header";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      produtos: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:3001/estoque").then(resposta => {
      this.setState({
        produtos: resposta.data
      });
    });
  }

  handleIncrement = addId => {
    const produtos = this.state.produtos.includes(c => c.id === addId);
    this.setState({ produtos });
  };

  handleDelete = estoqueId => {
    const produtos = this.state.produtos.filter(c => c.id !== estoqueId);
    this.setState({ produtos });
  };

  render() {
    return (
      <div>
        <MyHeader />

        {this.state.produtos.map(produto => (
          <Produto
            onDelete={this.handleDelete}
            key={produto.id}
            id={produto.id}
            descricao={produto.descricao}
            valor={produto.valor}
            quantidade={produto.quantidade}
          />
        ))}
        <div>
          <br></br>
          <br></br>

          <button
            onClick={() => this.props.handleIncrement(this.props.id)}
            className="add"
          >
            +
          </button>
        </div>
      </div>
    );
  }
}
export default Container;
