import React, { Component } from "react";
import axios from "axios";
import Produto from "./Produto";
import "./index.css";
import MyHeader from "./Header";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      produtos: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:3001/estoque").then(resposta => {
      this.setState({
        produtos: resposta.data
      });
    });
  }

  handleDelete = estoqueId => {
    const produtos = this.state.produtos.filter(c => c.id !== estoqueId);
    this.setState({ produtos });
  };

  handleIncrement = novo => {
    this.setState({
      produtos: [
        this.produto,
        { descricao: "ANKLEBOOT", valor: 100, quantidade: 10 },
        novo
      ]
    });
  };

  update = event => {
    const dados = {
      id: event.target,
      descricao: event.target,
      valor: this.state.valor,
      quantidade: this.state.quantidade
    };
    console.log(dados);
    axios
      .post("http://localhost:3001/updateCalcado", dados)
      .then(response => {
        console.log(response.data);
      })
      .catch(erro => console.log(erro));
  };

  render() {
    return (
      <div>
        <MyHeader />

        {this.state.produtos.map(produto => (
          <Produto
            onDelete={this.handleDelete}
            onUpdate={this.update}
            key={produto.id}
            id={produto.id}
            descricao={produto.descricao}
            valor={produto.valor}
            quantidade={produto.quantidade}
          />
        ))}
        <div>
          <br></br>
          <br></br>

          <button onClick={this.handleIncrement} className="add">
            +
          </button>
        </div>
      </div>
    );
  }
}
export default Container;
