        descricao: "",

      }
      );
    } else {
      produtos.push({
        _id: 1, descricao: "",

      })
    } this.setState({ produtos: produtos });
  };

  render() {
    return (
      <div>
        <MyHeader />

        {this.state.produtos.map(produto => (
          <Produto
            key={produto.id}
            _id={produto._id}
            id={produto.id}
            descricao={produto.descricao}
            valor={produto.valor}
            quantidade={produto.quantidade}
          />
        ))}
        <div>
          <br></br>
          <br></br>

          <button onClick={this.addOne} className="add">
            +
          </button>
        </div>
      </div>
    );
  }
}
export default Container;
