import React, { Component } from "react";
import axios from "axios";
import Produto from "./Produto";
import "./index.css";
import MyHeader from "./Header";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      produtos: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:3001/estoque").then(resposta => {
      this.setState({
        produtos: resposta.data
      });
    });
  }

  handleIncrement = novo => {
    this.setState({
      produtos: [{ descricao: "ANKLEBOOT", valor: 100, quantidade: 10 }, novo]
    });
  };

  onDelete = () => {
    const deleteData = { id: this.state.id };
    console.log();
    axios
      .delete(`http://localhost:3001/deleteCalcado/`, deleteData)
      .then(response => {
        console.log(response.data);
      })
      .catch(erro => console.log(erro));
  };

  render() {
    return (
      <div>
        <MyHeader />

        {this.state.produtos.map(produto => (
          <Produto
            key={produto.id}
            id={produto.id}
            descricao={produto.descricao}
            valor={produto.valor}
            quantidade={produto.quantidade}
          />
        ))}
        <div>
          <br></br>
          <br></br>

          <button onClick={this.handleIncrement} className="add">
            +
          </button>
        </div>
      </div>
    );
  }
}
export default Container;
