import React from "react";
import axios from "axios";

export default class Produto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: this.props.nome,
      preco: 0,
      quntidade: 0
    };
  }

  inserir = () => {
    console.log("aloha");
    const dados = {
      nome: this.state.nome,
      quantidade: this.state.quntidade,
      preco: this.state.preco
    };
    console.log(dados);
    // Aqui iria /updateEndereco
    axios
      .post("https://api.github.com/emojis", dados)
      .then(response => {
        console.log(response.data);
      })
      .catch(erro => console.log(erro));
  };

  render() {
    return (
      <div>
        <form action="#">
          <input
            type="text"
            value={this.state.nome}
            onChange={event => {
              this.setState({ nome: event.target.value });
            }}
          />
          <input
            type="text"
            value={this.state.preco}
            onChange={event => {
              this.setState({ preco: event.target.value });
            }}
          />
          <input
            type="number"
            value={this.state.quntidade}
            onChange={event => {
              this.setState({ quantidade: event.target.value });
            }}
          />
        </form>
        <button onClick={this.inserir}>Inserir/Atualizar</button>
        <button>Excluir</button>
      </div>
    );
  }
}
