import React from "react";
import axios from "axios";

export default class Produto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sapato: this.props.sapato,
      preco: this.props.preco,
      lote: this.props.lote
    };
  }

  render() {
    return (
      <div id="tabela">
        <form action="#" id="formu">
          Descrição/ /Valor <br></br>{" "}
          <input
            type="text"
            value={this.state.sapato}
            onChange={event => {
              this.setState({ descricao: event.target.value });
            }}
          />
          <input
            type="text"
            value={this.state.preco}
            onChange={event => {
              this.setState({ preco: event.target.value });
            }}
          />
          <input
            type="number"
            value={this.state.lote}
            onChange={event => {
              this.setState({ quantidade: event.target.value });
            }}
          />
        </form>

        <button onClick={this.inserir}>Inserir/Atualizar</button>
        <button>Excluir</button>
        <br></br>
      </div>
    );
  }
}
