import React, { Component } from "react";
import axios from "axios";

class Produto extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.id,
      descricao: this.descricao,
      valor: this.valor,
      quantidade: this.quantidade
    };
  }

  handleDelete = () => {
    console.log("event");
  };

  render() {
    return (
      <div id="tabela">
        Descrição______Valor__________Quantidade_____ <br></br>{" "}
        <input
          type="text"
          value={this.props.descricao}
          onChange={event => {
            this.setState({ descricao: event.target.value });
          }}
        />
        <input
          type="number"
          value={this.props.valor}
          onChange={event => {
            this.setState({ valor: event.target.value });
          }}
        />
        <input
          type="number"
          value={this.props.quantidade}
          onChange={event => {
            this.setState({ quantidade: event.target.value });
          }}
        />
        <button className="fat">Inserir/Atualizar</button>
        <button onClick={this.handleDelete} className="fat">
          Excluir
        </button>
        <br></br>
      </div>
    );
  }
}
export default Produto;
