import React, { Component } from "react";
import axios from "axios";
import Produto from "./Produto";
import "./index.css";
import MyHeader from "./Header";

class Container extends Component {
  constructor(props) {
    super(props);
    this.state = {
      produtos: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:3001/estoque").then(resposta => {
      this.setState({
        produtos: resposta.data
      });
    });
  }

  handleIncrement = novo => {
    <div id="tabela">
      Descrição____Valor__________Quantidade_____ <br></br>{" "}
      <form>
        <input
          type="text"
          value={this.state.descricao}
          onChange={event => {
            this.setState({ descricao: event.target.value });
          }}
        />
        <input
          type="number"
          value={this.state.valor}
          onChange={event => {
            this.setState({ valor: event.target.value });
          }}
        />
        <input
          type="number"
          value={this.state.quantidade}
          onChange={event => {
            this.setState({ quantidade: event.target.value });
          }}
        />
        <button
          onClick={() => {
            this.update();
          }}
          className="fat"
        >
          Inserir/Atualizar
        </button>
        <button
          onClick={event => {
            this.onDelete(event);
          }}
          className="fat"
        >
          Excluir
        </button>
        <br></br>
      </form>
    </div>;
  };

  render() {
    return (
      <div>
        <MyHeader />

        {this.state.produtos.map(produto => (
          <Produto
            key={produto.id}
            id={produto.id}
            descricao={produto.descricao}
            valor={produto.valor}
            quantidade={produto.quantidade}
          />
        ))}
        <div>
          <br></br>
          <br></br>

          <button onClick={this.handleIncrement} className="add">
            +
          </button>
        </div>
      </div>
    );
  }
}
export default Container;
