import React from "react";
import axios from "axios";

export default class Produto extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      descricao: this.props.descricao,
      valor: this.props.valor,
      quantidade: this.props.quantidade
    };
  }

  inserir = () => {
    console.log("");
    const dados = {
      descricao: this.props.descricao,
      valor: this.props.valor,
      quantidade: this.props.quantidade
    };
    console.log(dados);
    axios
      .post("", dados)
      .then(response => {
        console.log(response.data);
      })
      .catch(erro => console.log(erro));
  };

  render() {
    return (
      <div id="tabela">
        <form action="#" id="formu">
          Descrição_____Valor__________Quantidade_____ <br></br>{" "}
          <input type="text" value={this.props.descricao} />
          <input type="text" value={this.props.valor} />
          <input type="number" value={this.props.quantidade} />
        </form>

        <button class="fat" onClick={this.inserir}>
          Inserir/Atualizar
        </button>
        <button class="fat">Excluir</button>

        <br></br>
      </div>
    );
  }
}
